}

void ADDV (int m, int n)
{
  long dest, src, ans;

  if ((long)R[n] &gt;= 0)
    dest = 0;
  else
    dest = 1;

  if ((long)R[m] &gt;= 0)
    src = 0;
  else
    src = 1;

  src += dest;
  R[n] += R[m];

  if ((long)R[n] &gt;= 0)
    ans = 0;
  else
    ans = 1;

  ans += dest;

  if (src == 0 || src == 2)
  {
    if (ans == 1)
      T = 1;
    else
      T = 0;
  }
  else
    T = 0;

  PC += 2;
}

