void MACL (int m, int n)
{
  unsigned long RnL, RnH, RmL, RmH, Res0, Res1, Res2;
  unsigned long temp0, temp1, temp2, temp3;
  long tempm, tempn, fnLmL;

  tempn = Read_32 (R[n]);
  R[n] += 4;
  tempm = Read_32 (R[m]);
  R[m] += 4;

  if ((long)(tempn ^ tempm) &lt; 0)
    fnLmL = -1;
  else
    fnLmL = 0;

  if (tempn &lt; 0)
    tempn = 0 - tempn;
  if (tempm &lt; 0)
    tempm = 0 - tempm;

  temp1 = (unsigned long)tempn;
  temp2 = (unsigned long)tempm;

  RnL = temp1 &amp; 0x0000FFFF;
  RnH = (temp1 &gt;&gt; 16) &amp; 0x0000FFFF;
  RmL = temp2 &amp; 0x0000FFFF;
  RmH = (temp2 &gt;&gt; 16) &amp; 0x0000FFFF;
  temp0 = RmL * RnL;
  temp1 = RmH * RnL;
  temp2 = RmL * RnH;
  temp3 = RmH * RnH;

  Res2 = 0;

  Res1 = temp1 + temp2;
  if (Res1 &lt; temp1)
    Res2 += 0x00010000;

  temp1 = (Res1 &lt;&lt; 16) &amp; 0xFFFF0000;

  Res0 = temp0 + temp1;
  if (Res0 &lt; temp0)
    Res2++;

  Res2 = Res2 + ((Res1 &gt;&gt; 16) &amp; 0x0000FFFF) + temp3;

  if(fnLmL &lt; 0)
  {
    Res2 = ~Res2;
    if (Res0 == 0)
      Res2++;
    else
      Res0 = (~Res0) + 1;
  }

  if (S == 1)
  {
    Res0 = MACL + Res0;
    if (MACL &gt; Res0)
      Res2++;

    Res2 += MACH &amp; 0x0000FFFF;

    if (((long)Res2 &lt; 0) &amp;&amp; (Res2 &lt; 0xFFFF8000))
    {
      Res2 = 0xFFFF8000;
      Res0 = 0x00000000;
    }

    if (((long)Res2 &gt; 0) &amp;&amp; (Res2 &gt; 0x00007FFF))
    {
      Res2 = 0x00007FFF;
      Res0 = 0xFFFFFFFF;
    }

    MACH = (Res2 &amp; 0x0000FFFF) | (MACH &amp; 0xFFFF0000);
    MACL = Res0;
  }
  else
  {
    Res0 = MACL + Res0;
    if (MACL &gt; Res0)
      Res2 ++;

    Res2 += MACH;
    MACH = Res2;
    MACL = Res0;
  }

  PC += 2;
}

void MACW (int m, int n)
{
  long tempm, tempn, dest, src, ans;
  unsigned long templ;

  tempn = Read_16 (R[n]);
  R[n] += 2;
  tempm = Read_16 (R[m]);
  R[m] += 2;

  templ = MACL;
  tempm = ((long)(short)tempn * (long)(short)tempm);

  if ((long)MACL &gt;= 0)
    dest = 0;
  else
    dest = 1;

  if ((long)tempm &gt;= 0)
  {
    src = 0;
    tempn = 0;
  }
  else
  {
    src = 1;
    tempn = 0xFFFFFFFF;
  }

  src += dest;
  MACL += tempm;

  if ((long)MACL &gt;= 0)
    ans = 0;
  else
    ans = 1;

  ans += dest;

  if (S == 1)
  {
    if (ans == 1)
    {
      #if SH1
      if (src == 0 || src == 2)
        MACH |= 0x00000001;
      #endif

      if (src == 0)
        MACL = 0x7FFFFFFF;
      if (src == 2)
        MACL = 0x80000000;
    }
  }
  else
  {
    MACH += tempn;
    if (templ &gt; MACL)
      MACH += 1;

    #if SH1
    if ((MACH &amp; 0x00000200) == 0)
      MACH &amp;= 0x000003FF;
    else
      MACH |= 0xFFFFFC00;
    #endif
  }

  PC += 2;
}

