void LDSMFPSCR (int m)
{
  #if SH2E || SH3E
  FPSCR = Read_32 (R[m]) &amp; 0x00018C60;

  #elif SH4 || SH4A || SH2A
  FPSCR = Read_32 (R[m]) &amp; 0x003FFFFF;

  #endif

  R[m] += 4;
  PC += 2;
}

void STSMFPSCR (int n)
{
  R[n] -= 4;

  #if SH2E || SH3E
  Write_32 (R[n], FPSCR);

  #elif SH4 || SH4A || SH2A
  Write_32 (R[n], FPSCR &amp; 0x003FFFFF);

  #endif

  PC += 2;
}

void LDSFPUL (int m)
