      A1G = A1G | MASKFFFFFF00;
    negative_bit = DSP_ALU_DSTG_BIT7;
    zero_bit = (DSP_ALU_DST == 0) &amp; (DSP_ALU_DSTG_LSB8 == 0);
    break;
  }

